PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE users (
	id INTEGER NOT NULL, 
	email VARCHAR, 
	username VARCHAR, 
	password_hashed VARCHAR(128), 
	registered_on DATETIME, 
	mealplan VARCHAR, 
	PRIMARY KEY (id), 
	UNIQUE (email)
);
CREATE TABLE recipes (
	id INTEGER NOT NULL, 
	name VARCHAR(100), 
	directions VARCHAR, 
	ingredients VARCHAR, 
	calcium FLOAT, 
	calories FLOAT, 
	carbohydrate FLOAT, 
	cholesterol FLOAT, 
	fat FLOAT, 
	fiber FLOAT, 
	iron FLOAT, 
	monounsaturated_fat FLOAT, 
	polyunsaturated_fat FLOAT, 
	potassium FLOAT, 
	protein FLOAT, 
	saturated_fat FLOAT, 
	sodium FLOAT, 
	sugar FLOAT, 
	trans_fat FLOAT, 
	vitamin_a FLOAT, 
	vitamin_c FLOAT, 
	number_of_servings INTEGER, 
	type VARCHAR, 
	PRIMARY KEY (id)
);
CREATE TABLE exercise (
	id INTEGER NOT NULL, 
	name VARCHAR, 
	targetmusclegroups VARCHAR, 
	level INTEGER, 
	sets INTEGER, 
	reps INTEGER, 
	PRIMARY KEY (id)
);
COMMIT;
