INSERT INTO recipes VALUES(38,'Baked Cashew Chicken Strips','["Preheat oven to 350 \u00b0F (220 \u00b0C).", "Prepare baking sheet by covering in aluminium and spraying with 0 calorie oil spray.", "Put egg and a teaspoon of water in shallow bowl and beat well.", "Finely chop cashews. In shallow bowl combine cashews, panko bread crumbs, salt, paprika, pepper and garlic salt.", "Lightly salt and pepper each chicken tenderloin. Dip tenderloin in egg mixture to completely cover. Then dip tender in cashew panko mixture to completely cover.", "Place all tenders on baking sheet and spray tops with 0 calorie oil spray.", "Place baking sheet in oven for 25-35 minutes until light golden brown and chicken is completely cooked."]','["1/4 tsp garlic salt", "1/4 tsp salt", "1/4 cup cashews", "1/2 cup panko bread crumbs", "8 boneless skinless chicken tenderloins", "1 large egg", "1 tsp paprika", "1 dash black pepper"]',1.0,177.99999999999999999,8.6500000000000003552,112.99999999999999999,5.6799999999999997157,0.69999999999999995559,2.0,0.4829999999999999849,0.21399999999999999578,269.0,24.649999999999998578,1.0240000000000000213,505.00000000000000001,0.9000000000000000222,0.0,3.0,0.0,4,'"Main Dish"');
