INSERT INTO recipes VALUES(49,'Banana Bread with Cream Cheese Filling','["Pre-heat oven at 350\u00b0F.", "In a bowl mash bananas. Mix 1 egg, brown sugar, white sugar, butter, vanilla extract, and sour cream.", "Add flour, salt, and baking soda.", "To prepare the cream cheese filling, mix the remaining egg and cream cheese together.", "Pour half of the banana batter into a greased 9\" x 5\" loaf pan, then pour the cream cheese over. Add the remaining of the batter on top.", "Bake for 50 minutes or until toothpick comes out clean. Allow to cool for 15 minutes before serving."]','["1/4 cup sour cream", "2 large eggs", "4 oz original cream cheese", "120 g granulated sugar", "1 cup white flour", "1 unsalted butter stick", "128 g light brown sugar", "1 dash salt", "1 tsp vanilla extract", "2 medium bananas", "1 tsp baking soda"]',1.0,308.99999999999999999,40.509999999999998011,81.000000000000000003,15.560000000000000497,1.0,5.0,3.1230000000000002202,0.59399999999999997246,123.0,3.8799999999999998934,9.3100000000000004973,203.99999999999999999,28.269999999999999573,0.0,11.0,2.0,10,'["Dessert", "Baked", "Snack", "Breakfast"]');
INSERT INTO recipes VALUES(50,'Banana-Chocolate Breakfast Muffins','["Place flax seed in a small cup with 2 tbsp of water, let sit while you get everything else out. This''ll be the egg replacement.", "Preheat oven to 350 \u00b0F (175 \u00b0C).", "Grind oats in a food processor/blender to make a flour, should measure out to 1 cup of flour.", "Whisk wet ingredients together.", "Combine all dry ingredients and whisk into wet. Fold in chocolate chips.", "Grease a muffin pan and dole the batter out. Bake for 20-25 minutes, or until a toothpick comes out mostly clean.", "Serve with nothing or any desired condiments, I used peanut butter."]','["1/2 tsp baking powder", "1/4 tsp kosher salt", "1 rounded tbsp sprouted ground flax", "1/4 cup semi-sweet chocolate morsels", "1 cup oats", "1/3 cup applesauce", "1 tsp vanilla extract", "3 small bananas", "1/2 tsp baking soda", "1/4 cup cocoa powder unsweetened"]',0.0,80.0,15.189999999999999503,0.0,2.25,2.5,2.0,0.66400000000000003463,0.51000000000000000888,140.99999999999999999,1.8600000000000000976,0.90200000000000002398,100.0,6.2999999999999998223,0.0,0.0,2.0,12,'["Baked", "Snack", "Breakfast"]');
INSERT INTO recipes VALUES(51,'Banana Chocolate Waffles','["Blend the eggs, dates, banana, and milk.", "Add the cocoa powder and coconut flour. Stir well.", "Cook in in waffle maker until set and firm."]','["20 g organic coconut flour", "50 ml organic whole milk", "50 g pitted dates", "100 g banana", "3 large eggs", "30 g cocoa powder (unsweetened)"]',2.0,108.0,15.730000000000000426,105.99999999999999999,3.8599999999999998756,3.8999999999999999111,8.0,1.1859999999999999431,0.375,293.00000000000000001,5.5099999999999997868,1.6080000000000000959,39.0,9.080000000000000071,0.0,4.0,2.0,6,'["Dessert", "Snack", "Breakfast"]');
