INSERT INTO recipes VALUES(256,'Crustless Quiche Muffins','["Preheat oven to 350 \u00b0F (175 \u00b0C). Spray and line the muffin cups with the sliced meat.", "Mix together the remaining ingredients in a medium bowl.", "Slowly pour/scoop the mixture into each lined muffin cup (kinda tricky so spraying the cups keeps the spilled egg from sticking).", "Bake for 12-15 minutes or until egg mixture doesn''t move when shaken. Pop out and enjoy with extra Tabasco on the side."]','["1/4 diced onion", "2 tsps Tabasco sauce", "2 oz diced ham", "2/3 cup shredded colby & monterey jack cheese", "12 slices deli ham", "6 large eggs"]',1.0,105.99999999999999999,1.15999999999999992,127.99999999999999999,6.5,0.0,4.0,2.0560000000000000497,0.60099999999999997868,116.99999999999999999,10.169999999999999928,2.4470000000000000639,482.00000000000000001,0.28000000000000002664,0.0,4.0,0.0,12,'"Breakfast"');
