INSERT INTO recipes VALUES(294,'Fried Rice','["Heat the olive oil in a pan and add the cooked rice.", "Scramble the egg whites and whole egg together in a bowl and add to rice. Mix well.", "Add your favorite mixed vegetables.", "Cook until heated all the way through.", "Note: if making as a main dinner dish, cut up and add chicken. If too dry, add a little soy sauce or some chicken broth."]','["1 large egg", "2 large egg whites", "1 tbsp extra virgin olive oil", "8 oz mixed vegetables", "1 cup cooked brown rice"]',4.0,295.0,38.619999999999997442,105.99999999999999999,10.689999999999999502,6.2999999999999998223,12.0,6.205000000000000071,1.6060000000000000941,405.00000000000000001,12.77999999999999936,1.9790000000000000923,144.0,0.42999999999999999333,0.0,35.999999999999999999,12.999999999999999999,2,'["Main Dish", "Side Dish", "Lunch"]');
