INSERT INTO recipes VALUES(360,'Keto Krack','["Melt butter and mix in stevia. Bring to a boil and pull off heat.", "Stir in vanilla and salt.", "Stir in almonds and peanuts.", "Spread out on cookie sheet lined with parchment paper. Let sit and cool for 20 minutes.", "Melt chocolate chips and coconut oil. Stir until smooth.", "Add nuts back in and stir until well mixed.", "Spread back on cookie sheet lined with parchment paper. Refrigerate 30 minutes.", "Break apart into 12 pieces."]','["1/2 tsp pure vanilla extract", "2 tbsps stevia baking blend", "2 tbsps coconut oil", "1 cup salted peanuts", "12 oz sugar free chocolate chips", "5 tbsps butter", "2 dashes salt", "1 cup whole almonds"]',2.0,316.0,21.219999999999998863,12.999999999999999999,26.239999999999998436,4.0,3.0,7.5400000000000000355,3.1760000000000001563,151.99999999999999999,6.7999999999999998223,11.778999999999999915,66.999999999999999998,1.15999999999999992,0.0,5.0,0.0,12,'["Dessert", "Snack"]');
INSERT INTO recipes VALUES(361,'Keto Lazy Woman Waffles','["Whisk eggs and mayonnaise until thoroughly incorporated.", "Add Swerve and mix thoroughly.", "Add all dry ingredients.", "Let sit at least one minute (or cover and store in refrigerator).", "Heat up waffle iron.", "Cook for 1 1/2 minutes (alternatively, you can make pancakes)."]','["1 1/2 tbsps almond flour", "1 tbsp Swerve", "1 1/2 tbsps coconut flour", "3 tbsps mayonnaise", "3 large eggs", "1/2 tsp cinnamon", "1 tsp baking powder"]',7.0,169.0,8.5199999999999995736,165.99999999999999999,13.679999999999999715,2.2000000000000001776,5.0,3.3050000000000001598,5.0129999999999999005,51.999999999999999998,6.2300000000000004263,3.0390000000000001456,249.0,0.29999999999999998889,0.0,6.0,0.0,4,'["Baked", "Breakfast"]');
