INSERT INTO recipes VALUES(380,'Lime Chicken Crockpot','["Mix salsa, black beans and chopped cilantro.", "Add lime juice, garlic powder, cayenne pepper and some salt.", "Salt the chicken on both sides and add to the mix.", "Cook on low for 6-8 hours."]','["1/2 tsp garlic powder", "3/4 tsp cayenne pepper", "1/4 cup cilantro", "1 cup black beans", "2 cups hot salsa", "2 dashes salt", "2 tbsps lime juice", "3 boneless skinless chicken breasts"]',2.0,194.0,12.410000000000000142,68.000000000000000001,1.5900000000000000799,2.7999999999999998223,9.0,0.365999999999999992,0.38200000000000000621,10.0,29.699999999999999289,0.41999999999999998445,521.99999999999999998,2.7599999999999997868,0.022999999999999999611,1.0,5.0,6,'["Main Dish", "Lunch"]');
INSERT INTO recipes VALUES(381,'Linguine in White Clam Sauce','["Prepare pasta according to package directions.", "In a medium skillet, heat olive oil and saute onions and garlic for 3-5 minutes.", "Stir in wine; bring to boil for about 3 minutes to reduce liquid.", "Stir in clams, parsley and pepper; cook to heat through.", "Serve over cooked linguine pasta."]','["2 tsps minced garlic", "1/4 cup extra virgin olive oil", "2 fl oz dry table wine", "1 cup Italian parsley", "6 oz baby clams", "4 oz whole wheat linguine dry", "1/4 tsp black pepper", "1 medium onion"]',2.0,580.00000000000000001,50.850000000000001422,104.99999999999999999,30.079999999999998295,7.0,3.0,19.713999999999998635,3.6379999999999999005,119.0,22.309999999999998721,3.7490000000000001101,457.99999999999999999,5.1299999999999998934,0.0,0.0,5.0,2,'"Main Dish"');
