INSERT INTO recipes VALUES(387,'Low Carb Ham & Egg Boats','["Preheat oven to 400 \u00b0F (205 \u00b0C). Get muffin pan, spray oil of choice. Lay ham in there to make a cup", "Break egg and pour into ham cup.  Sprinkle cheese over egg.", "Add salt & pepper to taste, then bake in oven for 10-15 minutes."]','["2 tbsps shredded colby & monterey jack cheese", "2 thin slices deli ham", "1 extra large egg"]',3.0,195.0,1.7299999999999999822,275.99999999999999999,12.619999999999999218,0.0,8.0,3.5499999999999998223,1.106999999999999984,178.99999999999999999,16.510000000000001563,5.2149999999999998578,605.99999999999999999,0.4500000000000000111,0.0,9.0,0.0,1,'"Breakfast"');
INSERT INTO recipes VALUES(388,'Low Carb Pancakes','["Preheat non-stick frying pan/griddle to medium-high heat with a spritz of non-fat cooking spray.", "Add water and egg white to Magic Bullet or blender first (otherwise, dry ingredients will not blend properly).", "Add all dry ingredients with wet in blender. Blend for 30 seconds.", "Carefully and slowly pour 3-4 small pancakes (or 2 larger) onto frying pan or griddle. Cook on one side until bubbles, then flip and cook for 2-3 minutes.", "Serve with pancake syrup."]','["1/4 cup calorie free pancake syrup", "1/2 oz oatmeal", "1 scoop whey protein isolate", "1/4 cup egg white", "1/8 tsp cinnamon", "1 dash salt", "1/4 cup water", "1/4 tsp baking powder"]',6.0,152.99999999999999999,3.5299999999999998046,5.0,0.56000000000000005329,0.29999999999999998889,3.0,0.047000000000000000111,0.053999999999999999389,108.0,34.0,0.12800000000000000266,771.0,0.46999999999999997335,0.0,0.0,0.0,1,'"Breakfast"');
INSERT INTO recipes VALUES(389,'Low Carb Zucchini Muffins','["Preheat oven to 350 \u00b0F (175 \u00b0C).", "Grate the zucchini and melt the coconut oil in the microwave.", "Combine all ingredients except the zucchini in a bowl or a food processor and mix well.", "Stir in the zucchini.", "Line a muffin pan with paper cups and half fill each cup with the dough.", "Bake for 25 minutes or until lightly browned on top."]','["1/4 cup sweetener", "2-4 liquid stevia drops", "1 cup zucchini", "3/4 cup coconut flour", "5 tbsps virgin coconut oil", "6 large eggs", "1 tsp cinnamon", "1/4 tbsp ground nutmeg", "1/2 tsp baking soda"]',1.0,88.000000000000000001,4.7300000000000004263,85.0,7.2999999999999998223,2.1000000000000000888,2.0,1.1000000000000000888,0.27400000000000002131,27.999999999999999999,3.7999999999999998223,5.150999999999999801,70.0,1.3899999999999999023,0.0,3.0,0.0,15,'["Baked", "Snack", "Breakfast"]');
