INSERT INTO recipes VALUES(391,'Madeleines','["Pre-heat oven to 375\u00b0F.", "Mix wet ingredients. Slowly add flour and baking powder.", "Place tablespoons of mixture into prepared madeleine moulds.", "Bake for 15 minutes or until golden and cakes spring back when pressed in the center. Cool on a wire rack then serve."]','["1 large egg", "1/3 cup vegetable oil", "1/2 cup sugar", "1/2 cup 1% fat milk", "1 1/2 cups white flour", "2 tsps vanilla extract", "2 tsps baking powder (sodium aluminum sulfate, double acting)"]',3.0,98.000000000000000001,13.320000000000000283,11.0,4.2099999999999999644,0.29999999999999998889,3.0,1.4050000000000000266,1.9430000000000000603,25.0,1.5700000000000000621,0.61499999999999999111,58.000000000000000001,5.6900000000000003907,0.0,1.0,0.0,19,'["Dessert", "Snack", "Baked"]');
