INSERT INTO recipes VALUES(415,'Morning Glory Muffins','["Pre-heat oven to 375\u00b0F. Mix dry ingredients together, making a well in the center.", "Stir honey, oil, orange juice, eggs, and vanilla together.", "Add honey mixture to dry ingredients. Mix well to combine.", "Add carrots and raisins.", "Spoon batter into muffin cups 2/3 full.", "Bake for 20 minutes. Perform toothpick test. Let it cool then serve."]','["2 tsps pure vanilla extract", "1 tsp iodized salt", "1/2 cup vegetable oil", "1 cup raisins", "3 cups shredded carrots", "1 1/2 cups all purpose flour", "4 large eggs", "1 cup dark brown sugar", "2 cups whole wheat flour", "2 tsps cinnamon", "1 tsp nutmeg (ground)", "1 cup orange juice", "4 tsps baking soda", "1 cup honey"]',1.0,142.99999999999999999,26.469999999999998863,20.999999999999999999,3.7700000000000000177,1.3999999999999999111,2.0,1.0449999999999999289,1.5480000000000000426,83.999999999999999996,2.3700000000000001065,0.5909999999999999698,170.0,16.489999999999998437,0.0,0.0,4.0,36,'["Breakfast", "Baked", "Snack"]');
