INSERT INTO recipes VALUES(419,'Mushroom Rigatoni','["Dice onions and garlic. Chop mushrooms.", "Saut\u00e9 onions and garlic in olive oil until caramelized, then add mushrooms.", "Add a cup of water and seasonings.", "Cook down mixture and deglaze with wine.", "Add pasta and enough water to cover it. Simmer on medium high heat. Note: Add water in small amounts as needed to cook pasta, but not too much to avoid making the sauce runny. Sauce should be a little thick when pasta is done.", "Once the pasta is tender and the liquid is reduced, turn off heat then serve."]','["1 tsp salt", "1 medium onion", "1 lb rigatoni pasta", "1 tbsp garlic powder", "1 tbsp onion powder", "1 tbsp black pepper", "1 tsp turmeric (ground)", "3 tbsps olive oil", "6 cloves garlic", "16 fl oz red table wine", "1 lb mushrooms"]',1.0,323.0,48.549999999999997159,0.0,6.3399999999999998578,3.2999999999999998223,6.0,3.7080000000000001847,0.65200000000000002398,300.99999999999999999,9.4299999999999997157,0.74899999999999999911,301.99999999999999999,3.8999999999999999111,0.0,0.0,3.0,8,'["Main Dish", "Lunch"]');
