INSERT INTO recipes VALUES(576,'Spinach Broccoli Egg Muffins','["Pre-heat oven at 350\u00b0 F. Mix all ingredients together.", "Transfer mixture to a greased muffin tray.", "Bake for 15 mins. Let cool completely before scooping out."]','["1 cup almond flour", "1 cup shredded 2% milk reduced fat mozzarella cheese", "4 slices colby jack cheese", "1 cup unsweetened almond vanilla milk", "4 cups frozen chopped spinach", "11 large eggs", "3/4 cup chopped broccoli"]',12.0,240.0,5.080000000000000071,249.0,17.280000000000001136,2.7000000000000001776,7.0,6.8689999999999997726,2.3079999999999998294,195.0,16.350000000000001421,5.5339999999999998081,268.0,1.0,0.0,12.999999999999999999,7.0,10,'["Snack", "Breakfast", "Appetizer"]');
