INSERT INTO recipes VALUES(598,'Stir Fry Turkey with Rice','["Prepare rice according to packaging. Chop vegetables.", "Saut\u00e9 onion and garlic with olive oil on medium heat. Add turkey and bell pepper.", "Add Worcestershire sauce and black pepper. Simmer and stir occasionally.", "Toss in rice and mix to incorporate."]','["1/4 tsp ground black pepper", "1 tsp minced garlic", "1 tbsp extra virgin olive oil", "1 medium green bell pepper", "1 medium raw vidalia sweet onion", "4 tsps worcestershire sauce", "16 oz lean ground turkey breast", "240 g seven whole grains rice"]',0.0,289.00000000000000001,26.579999999999998293,70.0,6.3700000000000001065,3.1000000000000000888,0.0,2.5,0.50200000000000000177,90.0,29.839999999999999857,0.50100000000000000088,249.0,3.4599999999999999644,0.0,0.0,0.0,4,'"Lunch"');
INSERT INTO recipes VALUES(599,'Stir-fry Vegetables with Turkey Kielbasa','["Put the oil and minced garlic in a wok over medium heat.", "Add the sliced onions and zucchini. Cook for 5 minutes.", "Add the turkey and cook for 10 minutes.", "Add the chopped spinach and cabbage. Cook for 5 minutes."]','["6 oz turkey polska kielbasa", "1 tbsp olive oil", "1/2 head medium cabbage", "2 cloves garlic", "1/2 medium onion", "2 cups spinach", "1/2 medium zucchini"]',17.0,295.0,23.660000000000000141,51.999999999999999998,16.260000000000001563,6.7999999999999998223,16.0,4.9619999999999997442,0.95799999999999996269,906.00000000000000003,17.170000000000001705,3.2650000000000001243,835.99999999999999996,13.30000000000000071,0.0,17.999999999999999999,102.99999999999999999,2,'["Main Dish", "Lunch"]');
INSERT INTO recipes VALUES(600,'Strawberry Banana Creamsicle Smoothie','["Add all ingredients to blender.", "Blend on high until smooth and fully mixed. About a minute or so.", "Transfer to your favorite cup or jar and enjoy."]','["1 scoop french vanilla protein powder", "4 fl oz 100% orange juice", "1/2 cup frozen strawberries", "4 oz plain Greek yogurt", "1 medium banana", "1/2 cup spinach"]',2.0,453.00000000000000001,65.989999999999994885,60.0,8.9499999999999992894,6.4000000000000003552,4.0,0.040000000000000000832,0.11100000000000000144,1275.9999999999999999,31.219999999999998863,5.6410000000000000142,142.99999999999999999,45.990000000000001989,0.0,8.0,16.0,1,'["Beverage", "Breakfast"]');
INSERT INTO recipes VALUES(601,'Strawberry Bread','["Place the strawberries in a medium bowl and mix in sugar.", "Preheat oven to 350 \u00b0F (175 \u00b0C). Line an 8x4x2 1/2\" pan with parchment paper and set aside.", "In a large bowl, combine the flour, baking powder, baking soda, salt, and cinnamon. Make a well in the center and set aside.", "Whisk in the egg substitute and the olive oil into the bowl with the strawberry mixture.", "Add the strawberry mixture all at once to flour mixture. Stir just until moistened (the batter will be lumpy).", "Pour the batter into the pan with the parchment paper.", "Bake for 60 minutes. Check the bread after the first 30 minutes and cover with aluminum foil to prevent over browning.  Use a wooden toothpick inserted near the center to make sure it''s done (the toothpick should come out clean). Gently remove loaf from pan and cool on a wire rack for at least 10 minutes before slicing."]','["1/4 tsp kosher salt", "1 1/2 tsps baking powder", "1/2 tsp baking soda", "1/2 cup extra virgin olive oil", "1/2 cup egg substitute", "2 cups all purpose flour", "1 cup sugar", "1/2 tsp cinnamon", "1 1/2 cups strawberries, sliced"]',0.0,223.0,33.170000000000001705,0.0,9.0700000000000002842,1.1000000000000000888,1.0,6.5759999999999996234,0.97999999999999998223,75.0,3.1400000000000001243,1.2459999999999999964,159.0,18.289999999999999147,0.0,0.0,13.999999999999999999,12,'["Baked", "Dessert", "Breakfast"]');
INSERT INTO recipes VALUES(602,'Strawberry Pineapple Protein Breakfast Shake','["Fill blender with strawberries.", "Cover strawberries with water, add some ice, nectar and whey protein.", "Blend well."]','["1 tbsp amber agave nectar", "1 scoop vanilla whey protein", "5 oz pineapple chunks", "1 cup strawberry halves"]',2.0,289.00000000000000001,50.670000000000001705,50.0,1.9599999999999999644,6.0,4.0,0.06500000000000000222,0.23599999999999998756,523.0,21.019999999999999574,1.0229999999999999094,51.999999999999999998,39.079999999999998293,0.0,0.0,98.999999999999999996,1,'["Beverage", "Breakfast"]');
INSERT INTO recipes VALUES(603,'Strawberry Protein Flax Smoothie','["Pour all ingredients into blender in the following order - milk, flax, sugar, yogurt, strawberries and then 2 cups of ice cubes.", "Blend on high speed until completely smooth and you hear no ice or frozen fruit rattling in the unit. (You may need to stir once with long spoon to redistribute ingredients).", "Pour into glasses, garnish with a fresh strawberry or sprig of mint and serve immediately."]','["1 tbsp ground flax seed", "1/2 cup nonfat vanilla Greek yogurt", "1 cup unsweetened vanilla almond milk", "3 cups frozen strawberries", "1/3 cup granulated sugar"]',0.0,138.99999999999999999,29.5,0.0,1.6200000000000001065,2.8999999999999999111,0.0,0.625,0.625,88.000000000000000001,3.75,0.0,55.999999999999999999,23.359999999999999432,0.0,0.0,0.0,4,'["Snack", "Beverage", "Breakfast"]');
INSERT INTO recipes VALUES(604,'Stromboli','["Heat oven to 400 \u00b0F (205 \u00b0C). Spray 2 cookie sheets with cooking spray.", "Brown ground beef until no longer pink. Drain and set aside.", "Unroll each dough, place one on each sprayed cookie sheet. Starting at center, press out each dough with hands to form 12\"x8\" rectangle.", "Spread half the sauce on each dough to within 2\" of the long side and 1/2\" of the short side. Place half the ground beef lengthwise down the center of each dough forming 3\" wide strip to within 1/2\" of short sides. Top each with half the cheese and half Italian seasoning.", "Fold the long sides of each dough over filling, pressing edges to seal. Bake for 10-15 minutes or until crusts is golden brown."]','["1/2 tsp Italian seasoning", "1/2 cup pizza sauce", "2 cups reduced fat mozzarella cheese", "1 lb 93/7 lean ground beef", "27.8 oz pizza crust dough"]',0.0,268.0,31.329999999999998295,32.0,7.830000000000000071,0.2000000000000000111,0.0,0.5,0.5,32.999999999999999999,18.170000000000001705,3.1669999999999998152,667.0,5.5,0.0,0.0,0.0,12,'["Main Dish", "Lunch"]');
INSERT INTO recipes VALUES(605,'Stuffed Collars','["Wash fresh collard green leaves and remove hard stems leaving as much of the leaf in tact. Set aside.", "In large bowl, combine ground beef, uncooked rice, chopped onions, salt and pepper.", "Take about a handful of the meat mixture and place at the bottom of the collard leaf then roll. Fold the side in and continue to roll. Do this for each leaf.", "Place the hard stems from the collard green at the bottom of a deep pot then layer the rolls tightly.", "Fill the pot with water just to the top of the rolls. Add olive oil.", "Bring the pot to a boil for 5 minutes then reduce the heat to low and cook for about 40 minutes."]','["1/4 tsp ground black pepper", "1 tsp iodized salt", "1/2 cup olive oil", "2 cups chopped onions", "3 cups white rice (long-grain)", "32 oz cooked collards", "3 lbs ground beef (85% lean / 15% fat)"]',11.0,534.99999999999999999,43.310000000000002272,76.999999999999999998,28.030000000000001136,3.0,20.999999999999999999,14.794999999999999928,2.0819999999999998507,511.99999999999999998,26.170000000000001704,8.2859999999999995878,330.99999999999999999,1.4899999999999999911,1.0600000000000000532,35.0,17.0,12,'["Main Dish", "Lunch"]');
INSERT INTO recipes VALUES(606,'Stuffed Green Pepper Soup','["Brown and crumble ground beef in a pot large enough to hold 10 cups, drain off grease.", "While beef is draining, saut\u00e9 onions and peppers for 3 minutes. Add garlic and saut\u00e9 for 1 more minute.", "Return ground beef to pot along with the rest of the ingredients except for the rice. Simmer for 15 minutes.", "Add rice to pot and simmer for 10 minutes or until rice is cooked.", "Note: makes 10 cups = 10 servings."]','["1 tsp salt", "2 beef bouillon cubes", "1 1/4 cups tomato puree", "1 tbsp minced garlic in water", "2 tbsps light brown sugar", "1/2 cup dry white rice", "15 oz petite cut diced tomatoes", "1/4 tsp black pepper", "1 tbsp worcestershire sauce", "1 cup onions, chopped", "1 1/2 cups green peppers, chopped", "3 cups water", "1 lb ground beef 85/15"]',1.0,165.0,14.339999999999999858,30.999999999999999999,6.8600000000000003197,2.6000000000000000888,7.0,2.9809999999999998721,0.22600000000000000643,417.0,10.759999999999999787,2.6800000000000001598,395.0,5.1500000000000003552,0.423999999999999988,0.0,22.0,10,'["Soup", "Lunch"]');
INSERT INTO recipes VALUES(607,'Stuffed Jalapenos','["Slice long ways and scrape inside out of peppers.", "Put cream cheese, cheddar cheese and paprika in bowl and heat till melted.", "Put cheese mixture in pepper halves.", "Wrap stuffed peppers in bacon secure with tooth pick.", "Cook at 400 \u00b0F (205 \u00b0C) for 25 minutes or till bacon fully done."]','["10 thin slices bacon", "3 oz sharp cheddar cheese", "1 lb cream cheese", "1/2 tsp paprika", "10 jalapeno peppers"]',1.0,111.0,1.080000000000000071,30.999999999999999999,10.349999999999999645,0.2000000000000000111,2.0,3.0720000000000000639,0.43199999999999999511,60.999999999999999999,3.7900000000000000355,6.081000000000000405,151.99999999999999999,0.28999999999999998001,0.0,10.0,3.0,20,'["Appetizer", "Side Dish"]');
INSERT INTO recipes VALUES(608,'Stuffed Mushrooms','["Clean the mushrooms. Remove the stems and set aside.", "Spread the olive oil in a small glass baking pan. Place the mushroom caps evenly in the pan, rolling them in the olive oil so they are lightly coated, bottom side up. Sprinkle lightly with salt.", "Chop the stems really fine and place them in a bowl. Chop the onion and garlic, add to stems.", "Add the egg and spices, mix together. Add enough parmesan cheese so that the mushroom stem mixture isn''t too runny. Add more if desired.", "Fill the caps with the mushroom/cheese mixture and bake on 350 \u00b0F (175 \u00b0C) for 15-20 minutes.", "Let cool 10 minutes before serving."]','["1/2 cup parmesan cheese, grated", "1 large egg", "1/4 tsp leaves basil", "1/4 tsp pepper", "1/2 tsp salt", "2 tbsps olive oil", "1 clove garlic, chopped", "18 medium mushrooms", "1/4 cup chopped red onion"]',16.0,206.99999999999999999,6.1799999999999997157,85.0,15.820000000000000283,1.3999999999999999111,6.0,8.6020000000000003126,1.5569999999999999396,413.99999999999999998,12.060000000000000497,4.7030000000000002913,671.99999999999999998,2.6400000000000001243,0.0,5.0,4.0,3,'["Appetizer", "Side Dish", "Lunch"]');
INSERT INTO recipes VALUES(609,'Stuffed Peppers','["Cut the top off the bell peppers and remove the seeds. Place upright in a small casserole dish with 1/2 cup water in the dish. Cover loosely with plastic wrap. Microwave for 2 minutes.", "Prepare quinoa as directed on the package using the beef stock.", "Saut\u00e9 chopped onions in a skillet with olive oil. Season with salt. When tender, add the ground beef and brown. Add chopped garlic and parsley. Season well with black pepper and paprika, to taste.", "Add the tomato sauce and simmer a few minutes to blend the flavors. Add the cooked quinoa.", "Stuff the beef and quinoa mix into the bell peppers. Bake in oven at 350 \u00b0F (175 \u00b0C) for 30 minutes."]','["1 tsp sea salt", "1 cup cooked quinoa", "2 tsps paprika", "1 tsp black pepper", "1 cup beef stock", "3 large bell peppers", "1 tsp garlic", "1/2 cup onion, chopped", "1 tbsp parsley", "1 lb ground beef 90/10", "1 cup tomato sauce"]',3.0,216.0,17.280000000000001136,48.000000000000000001,8.5399999999999991473,3.3999999999999999111,20.0,3.4529999999999998472,0.72899999999999998134,438.99999999999999998,18.059999999999998721,3.1890000000000000568,688.99999999999999998,5.9299999999999997157,0.47699999999999997957,17.0,182.0,6,'["Main Dish", "Lunch"]');
INSERT INTO recipes VALUES(610,'Stuffed Pepper Soup','["Brown the ground beef in a skillet.", "Place all ingredients in a crockpot and cook for approximately 6 hours on low.", "Note: use yellow, red and orange bell peppers to make the soup more colorful."]','["2 tsps minced garlic", "1/2 cup young green onions, chopped", "1 1/4 cups diced tomatoes & green chilies", "1 3/4 cups tomato sauce", "2 cups brown rice", "3 medium bell peppers", "96% lean ground beef", "3 cups beef broth", "1/2 tsp ground oregano", "1/2 tbsp basil"]',1.0,130.99999999999999999,15.0,24.0,2.5299999999999998046,1.8000000000000000444,2.0,0.005000000000000000104,0.066000000000000003108,91.000000000000000003,12.660000000000000142,0.82599999999999995647,569.99999999999999998,2.6800000000000001598,0.0,7.0,76.999999999999999998,10,'["Soup", "Lunch"]');
INSERT INTO recipes VALUES(611,'Sun-Dried Tomato Rosemary Scones','["Process all ingredients in a food processor or by hand (without herbs) for 30 seconds. Add tomatoes and rosemary and incorporate them.", "Gather the dough into a ball and form into circle on a piece of parchment paper. Use a knife to cut 8 triangles.", "Bake at 375 \u00b0F (190 \u00b0C) for about 20 minutes turning upside down once midway through."]','["1/2 tsp sea salt", "1 tbsp apple cider vinegar", "3 tbsps coconut flour", "2 tbsps extra virgin olive oil", "1 1/2 fl oz coconut milk", "1 3/4 cups almond flour", "1 medium egg", "2 tsps dried rosemary", "1/4 cup sun-dried tomatoes", "1/2 tsp baking soda"]',1.0,201.99999999999999999,8.0500000000000007105,22.999999999999999999,17.329999999999998294,3.8999999999999999111,2.0,2.6890000000000000568,0.45600000000000001643,68.000000000000000001,6.6299999999999998934,2.5409999999999999253,282.99999999999999999,1.8600000000000000976,0.0,1.0,1.0,8,'["Side Dish", "Baked", "Snack"]');
INSERT INTO recipes VALUES(612,'Super Summer Kale Salad','["Chop kale (stems removed).", "In a large bowl add kale, onion, carrots, blueberries, dried cranberries, cashews (smashed into bits) and shelled sunflower seeds. Set aside.", "Whisk together oil, vinegar, salt, pepper and sugar to make dressing.", "Pour dressing onto salad and toss.", "Refrigerate for about an hour before serving."]','["1/2 tsp salt", "1/4 cup extra virgin olive oil", "1/2 cup cashew halves & pieces", "1/2 cup hulled roasted unsalted hulled sunflower seeds", "1/2 cup apple cider vinegar", "1 cup shredded carrots", "1/2 cup dried cranberries", "1/3 cup sugar", "1/2 tsp black pepper", "6 tbsps blueberries", "8 oz kale", "1 small red onion"]',4.0,249.0,28.579999999999998294,0.0,15.080000000000000071,3.2000000000000001776,5.0,5.6299999999999998934,3.7259999999999999786,179.99999999999999999,4.2000000000000001776,2.1859999999999999431,196.0,18.609999999999999431,0.0,24.0,40.0,8,'["Side Dish", "Salad and Salad Dressing", "Lunch"]');
