INSERT INTO recipes VALUES(614,'Sweet Potato & Apple Bake','["Pre-heat oven to 375 \u00b0F (190 \u00b0C).  Peel and cube sweet potatoes and apples.", "In separate bowl combine brown sugar, maple syrup & orange zest + juice from 1/2 of orange. Mix.", "Grease an 8x8\" or 9x11\" pan.", "Combine sweet potatoes and apples with brown sugar, mix well.", "Cover with aluminum foil. Bake at 375 \u00b0F for 30 minutes.", "After 30 minutes remove foil, bake additional 15 minutes. Enjoy!"]','["2 medium granny smith apples", "1 tsp cinnamon", "1/2 orange", "3 5\" long sweet potatoes", "1/4 cup brown sugar", "2 tbsps maple syrup"]',5.0,188.99999999999999999,47.060000000000002272,0.0,0.22000000000000000111,5.2999999999999998223,7.0,0.018999999999999999528,0.066000000000000003108,413.00000000000000001,1.8899999999999999023,0.045999999999999999222,59.0,27.460000000000000853,0.0,76.999999999999999998,16.0,4,'"Side Dish"');
INSERT INTO recipes VALUES(615,'Sweet Potato Home Fries','["Preheat your skillet over medium-high heat.", "Peel and dice the potato.", "Add coconut oil to the pan and add potatoes with salt and pepper. Stir every minute or so to get some color on the potatoes", "Dice the onion.", "When the potatoes have cooked a little on all sides, add the onion and chili powder. Reduce heat to low-medium.", "Cook, stirring, until potatoes have a little color and onions are soft. Divide and serve."]','["1 dash sea salt", "1 tbsp coconut oil", "1/3 tsp chili powder", "1 dash black pepper", "1/2 cup onion, chopped", "1 1/2 cups sweet potato, cubed"]',3.0,163.0,24.379999999999999004,0.0,6.9500000000000001776,3.7000000000000001776,4.0,0.41999999999999998445,0.19400000000000000577,401.99999999999999999,2.0,5.9240000000000003765,137.99999999999999999,5.9100000000000001421,0.0,79.0,6.0,2,'["Side Dish", "Breakfast"]');
INSERT INTO recipes VALUES(616,'Sweet Red Cabbage','["Cut the cabbage in half and remove the stem. Slice cabbage very thinly.", "Put cabbage into a large pot with the balsamic vinegar, olive oil, raisins and water Stir to coat cabbage.", "Cover the pot and cook over medium heat until tender, about 15 minutes. Stir occasionally.", "Add lemon juice, salt and pepper to taste."]','["1 tbsp balsamic vinegar", "2 tbsps olive oil", "1 small red cabbage", "1/2 tsp pepper", "1/2 tsp salt", "0.12 fl oz lemon juice", "1/2 cup raisins", "1/3 cup water"]',4.0,112.99999999999999999,18.350000000000001421,0.0,4.7199999999999997513,2.6000000000000000888,6.0,3.3100000000000000532,0.5989999999999999769,335.99999999999999999,1.7900000000000000355,0.66300000000000003375,221.0,12.179999999999999715,0.0,6.0,60.999999999999999999,6,'"Side Dish"');
INSERT INTO recipes VALUES(617,'Swiss Chicken','["Preheat oven to 350 \u00b0F (175 \u00b0C). Spray 9\"x13\" baking dish with cooking spray.", "Lay chicken in bottom of prepared pan. Place cheese slices over chicken.", "In a bowl mix together soup and water. Spoon soup mixture over chicken. Sprinkle dry stuffing mix over soup layer and drizzle butter over top.", "Cover and bake 30-35 minutes or until chicken is done."]','["1/4 cup light butter", "6 slices Swiss cheese", "6 oz stuffing mix for turkey", "10 1/2 oz low fat cream of chicken soup", "1 1/2 lbs boneless skinless chicken breast", "1/4 cup water"]',0.0,348.0,24.749999999999999999,90.0,13.669999999999999928,1.0,0.0,2.875,1.0829999999999999626,228.99999999999999999,31.420000000000001705,6.0419999999999998152,885.99999999999999996,2.4199999999999999289,0.0,0.0,0.0,6,'"Main Dish"');
